with recursive tid as (select title_id from titles where primary_title = 'House of the Dragon'), ot as (select distinct title, dense_rank() over (order by title) as rn from akas where akas.title_id in (select title_id from tid)), ans as (select rn as num, title from ot where rn = 1 union all select ot.rn, ans.title || ',' || ot.title from ot join ans where ot.rn = ans.num + 1) select title from ans order by num desc limit 1;
