with pid as (select person_id from people as p where p.name = 'Nicole Kidman' and p.born = 1967), tid as (select title_id from crew as c where c.person_id in (select person_id from pid)), npid as (select person_id from crew as c where c.title_id in (select title_id from tid) and c.category in ('actor', 'actress')) select distinct p.name from people as p where p.person_id in (select person_id from npid);
